const mathematicianImages = [
    "data:image/jpeg;base64,",       // Albert Einstein
    "data:image/jpeg;base64,",           // Alan Turing
    "data:image/jpeg;base64,",             // Ada Lovelace
    "data:image/jpeg;base64,",                        // Pitágoras
       // Carl Friedrich Gauss
    
];

const mathematicianAltTexts = [
    "Foto de Albert Einstein",
    "Foto de Alan Turing",
    "Foto de Ada Lovelace",
    "Foto de Blaise Pascal",
    "Foto de Pitágoras",
    "Foto de Carl Friedrich Gauss",
    "Foto de Sophie Germain"
];

function getRandomInt(max) {
    return Math.floor(Math.random() * max);
}

function setRandomMathematicianImage() {
    const randomIndex = getRandomInt(mathematicianImages.length);
    const imageElement = document.getElementById('mathematician-image');
    imageElement.src = mathematicianImages[randomIndex];
    imageElement.alt = mathematicianAltTexts[randomIndex];
}

// Função para simular um atraso (tela de loading)
function showLoadingScreen() {
    const loadingScreen = document.getElementById('loading-screen');
    loadingScreen.classList.remove('hidden');
    // Adiciona uma pequena margem para garantir que a imagem troque antes do loading sumir
    setTimeout(() => {
        loadingScreen.classList.add('hidden');
    }, 2000); // Exibe por 2 segundos
}

// Função para calcular a expressão (com resultados aleatórios e o correto "escondido")
function calculate() {
    showLoadingScreen(); // Mostra a tela de carregamento
    setRandomMathematicianImage(); // Troca a foto ao calcular

    const expressionInput = document.getElementById('expression');
    const resultInput = document.getElementById('result');
    const trueResultElement = document.getElementById('true-result');
    const revealButton = document.getElementById('reveal-button');
    const revealText = document.getElementById('reveal-text');
    const expression = expressionInput.value;

    // Resetar o estilo do resultado verdadeiro para escondê-lo novamente a cada cálculo
    trueResultElement.style.fontSize = '0.1em';
    trueResultElement.style.color = 'transparent';
    trueResultElement.style.opacity = '0.01';
    trueResultElement.style.position = 'absolute';
    trueResultElement.style.bottom = '5px';
    trueResultElement.style.right = '5px';
    trueResultElement.style.textShadow = '0 0 0px #f0f2f5';
    trueResultElement.style.userSelect = 'none';
    trueResultElement.style.pointerEvents = 'none';
    revealButton.style.display = 'inline-block'; // Garante que o botão reapareça
    revealText.style.display = 'block'; // Garante que o texto reapareça

    // Simula o cálculo da expressão para obter o resultado correto
    let trueCalculatedResult;
    try {
        // Usa eval() com MUITA CAUTELA e apenas para este exemplo de "brincadeira".
        // Em aplicações reais, NUNCA use eval() com entrada de usuário sem validação robusta.
        trueCalculatedResult = eval(expression);
        if (isNaN(trueCalculatedResult) || !isFinite(trueCalculatedResult)) {
            throw new Error("Cálculo inválido.");
        }
    } catch (e) {
        trueCalculatedResult = "Erro na expressão!";
    }

    // Gera um resultado aleatório
    const randomResult = Math.floor(Math.random() * 1000) + 1; // Número aleatório entre 1 e 1000

    // Após o carregamento, exibe os resultados
    setTimeout(() => {
        resultInput.value = randomResult;
        trueResultElement.textContent = `A verdadeira resposta é: ${trueCalculatedResult}`;
    }, 2000); // Garante que a atualização ocorra após o loading sumir
}

// Ocultar a tela de carregamento e configurar o botão de revelação quando a página carregar pela primeira vez
document.addEventListener('DOMContentLoaded', () => {
    document.getElementById('loading-screen').classList.add('hidden');
    setRandomMathematicianImage(); // Define a imagem inicial

    const revealButton = document.getElementById('reveal-button');
    const trueResultElement = document.getElementById('true-result');
    const revealText = document.getElementById('reveal-text');

    // Listener para o botão de revelar
    revealButton.addEventListener('click', () => {
        trueResultElement.style.fontSize = '1em';
        trueResultElement.style.color = '#333';
        trueResultElement.style.opacity = '1';
        trueResultElement.style.position = 'static'; // Remove a posição absoluta para fluir no layout
        trueResultElement.style.bottom = 'auto';
        trueResultElement.style.right = 'auto';
        trueResultElement.style.textShadow = 'none';
        trueResultElement.style.userSelect = 'auto';
        trueResultElement.style.pointerEvents = 'auto';
        revealText.style.display = 'none'; // Oculta a instrução após o clique
        revealButton.style.display = 'none'; // Oculta o botão após o clique
    });
});